import React, { useState, useEffect } from 'react'
import io from 'socket.io-client'
import TextField from '@material-ui/core/TextField'
import Container from '@material-ui/core/Container'

import Navbar from '../components/navbar.js'
import '../css/home.css'


const socket = io.connect('http://localhost:4000')


function Home() {  

  //déclaration states
  const [mess, setMess] = useState({ message: '', name: '' })
  const [chat, setChat] = useState([])

  //déclenché en écrivant, met à jour la state message
  const onTextChange = e => {
    setMess({ ...mess, [e.target.name]: e.target.value })
  }

  //déclenché par le form, envoi les infos au serveur
  const onMessageSubmit = e => {
    e.preventDefault()
    const { name, message } = mess
    socket.emit('message', { name, message })
    setMess({ message: '', name })
  }

  //déclenchement auto, récup évènement général message (et évite les boucles infinies)
  useEffect(() => {
    socket.on('message', ({ name, message }) => {
      setChat([...chat, { name, message }])
    })
  })

  //////TEST//////
  const user = "Un nouveau disputeur"
  const [phrase, setPhrase] = useState('')

  const [count, setCount] = useState(0)
  if(count==0){
    console.log("okay!")
  }
  console.log(count)


  socket.emit('newUser', user)

  useEffect(() => {
    socket.on('welcome', phrase =>{
      setPhrase(phrase)
    })
  })

  ////////////////


  //fin du render
  const renderChat = () => {
    return chat.map(({ name, message }, index) => (
      <div key={index}>
        <h3>
          {name} : <span>{message}</span>
        </h3>
      </div>
    ))
  }

  return (
    <div>
    <Navbar/>
    <Container>
    <div className="card">
      <form onSubmit={onMessageSubmit}>
        <h1>Messanger</h1>
        <div className="name-field">
          <TextField
            name="name"
            onChange={e => onTextChange(e)}
            value={mess.name}
            label="Name"
          />
        </div>
        <div>
          <TextField
            name="message"
            onChange={e => onTextChange(e)}
            value={mess.message}
            id="outlined-multiline-static"
            variant="outlined"
            label="Message"
          />
        </div>
        <button>Send Message</button>
      </form>
      <div className="render-chat">
        <h1>Chat Log</h1>
        {phrase}
        {renderChat()}
      </div>
    </div>
    </Container>
    </div>
  )
}

export default Home
