import React, { useState, useEffect } from 'react'
import "bootstrap/dist/css/bootstrap.min.css"
import '../css/home.css'
import socket from './connect.js'

function Chat(){

    const [chatArray, setChatArray] = useState([])
    const today = new Date()
    //date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();



    useEffect(() => {
        socket.on('message', ({ name, message, type }) => {
          setChatArray([...chatArray, { name, message, type }])
        })
    })

    const [actualRoom, setActualRoom] = useState([])
    socket.on('actualRoom', room => {
      setActualRoom(room)
    })


    const renderChat = () => {
        console.log(today)
        return chatArray.map(({ name, message, type }, index) => (
          <div key={index}>
            <h4 className={type}>
              {name} <br></br>
              <span className="text_mess"> {message}</span>
            </h4>
          </div>
        ))
      }

    return(
        <div>
            <h3 className="titleChat">{chatArray.name}Chat {actualRoom}</h3>
            <div className="chat_size">
                {renderChat()}
            </div>
        </div>
    )
}

export default Chat
