import React, { useState } from 'react'
import io from 'socket.io-client'
import Container from '@material-ui/core/Container'
import "bootstrap/dist/css/bootstrap.min.css"
import { Row, Form, Button } from 'react-bootstrap';
import '../css/login.css'
import logo from '../assets/logo.jpg';
import {Redirect} from 'react-router-dom'

const socket = io.connect('http://localhost:4000')

function Login(){

  state = {
    redirect: false
  }
  setRedirect = () => {
    this.setState({
      redirect: true
    })
  }
  renderRedirect = () => {
    if (this.state.redirect) {
      return <Redirect to='/home' />
    }
  }

  const [login, setLogin] = useState({login: ''})

  const handleChange = e => {
    setLogin(e.target.value)
  }

  const onLoginSubmit = e => {
    e.preventDefault()
    console.log(login)
    socket.emit('loginEnter', login)
    this.renderRedirect
  }

  return (
    <Container>
      <Row className="justify-content-md-center">
        <h1>Start a new chat on My_Dispute</h1>
      </Row>
      <Row className="justify-content-md-center">
        <Form id="color" onSubmit={onLoginSubmit}>
          <Form.Group controlId="Login">
            <Form.Label>Login</Form.Label>
            <Form.Control name ="login" onChange={handleChange} type="text" required={true}/>
          </Form.Group>

          <div className="buttonLog">
          <a href="/home"><Button variant="dark" type="submit">Login</Button></a>
          </div>
        </Form>
      </Row>
      <div className="welcome_logo">
          <img className="images" src={logo} alt="logo"/>
      </div>
    </Container>
  )
}

export default Login
