import React, { useState, useEffect } from 'react'
import "bootstrap/dist/css/bootstrap.min.css"
import { Container, Row, Col, Jumbotron, Form, Button } from 'react-bootstrap';
import '../css/home.css'
import socket from './connect.js'

function Chat(){

    const [chatArray, setChatArray] = useState([])

    useEffect(() => {
        socket.on('message', ({ name, message, type }) => {
          setChatArray([...chatArray, { name, message, type }])
        })
    })

    const renderChat = () => {
        return chatArray.map(({ name, message, type }, index) => (
          <div key={index}>
            <h4 className="send">
              {name} <br></br>
              <span className="text_mess"> {message}</span>
            </h4>
          </div>
        ))
      }

    return(
        <div>
            <h3 className="titleChat">Chat My_Dispute</h3>
            <div className="chat_size">
                {renderChat()}
            </div>
        </div>
    )
}

export default Chat
