import React from 'react'
import {BrowserRouter as Router, Switch, Route} from "react-router-dom"
import Welcome from '../components/welcome.js'
import Login from '../components/login.js'
import Other from '../components/other.js'
import Home1 from '../components/home1.js'
import socket from './connect.js'


function Navigator() {

  const [chatArray, setChatArray] = useState([])

    useEffect(() => {
        socket.on('message', ({ name, message, type }) => {
          setChatArray([...chatArray, { name, message, type }])
        })
    })

    const renderChat = () => {
      return chatArray.map(({ name, message, type }, index) => (
        <div key={index}>
          <h4 className={type}>
            {name} <br></br>
            <span> {message}</span>
          </h4>
        </div>
      ))
    }

    return  (
      <Router>
          <Switch>
            <Route exact path="/" component={Welcome} />
            <Route exact path="/login" component={Login} />
            <Route path="/other" component={Other} />
            <Route path={mess.name} component={Home1} />
          </Switch>
      </Router>
    )
}

export default Navigator
