import React, { useState, useEffect } from 'react'
import "bootstrap/dist/css/bootstrap.min.css"
import { Container, Row, Col, Jumbotron, Form, Button } from 'react-bootstrap';
import '../css/home1.css'
import socket from './connect.js'

function Chat(){

    const [chatArray, setChatArray] = useState([])

    useEffect(() => {
        socket.on('message', ({ name, message, type }) => {
          setChatArray([...chatArray, { name, message, type }])
        })
    })

    const renderChat = () => {
        return chatArray.map(({ name, message, type }, index) => (
          <div key={index}>
            <h4 className={type}>
              {name} : <span>{message}</span>
            </h4>
          </div>
        ))
      }


    return(
        <div>
            <h3 className="title">Chat My_Dispute</h3>
            {renderChat()}
        </div>
    )
}

export default Chat
