    // Affiche List Room
    const [listRooms, setListRooms] = useState([])
    socket.on('listRooms', listRooms => {
      console.log(listRooms)
      setListRooms(listRooms, listRooms)
    })

    // Room join
    const [listRoomsUser, setListRoomsUser] = useState([])
    socket.on('listRoomsUser', listRoomsUser => {
      setListRoomsUser(listRoomsUser, listRoomsUser)
      console.log("user Rooms :")
      console.log(listRoomsUser)
    })
    const onRoomJoin = e => {
      e.preventDefault()
      console.log()
      //socket.emit('joinRoom', {listRooms: listRooms, )
    }

    // Room Leave

    socket.on('listRoomsUser', listRoomsUser => {
      setListRoomsUser(listRoomsUser, listRoomsUser)
    })
    const onRoomLeave = e => {
      e.preventDefault()
      //socket.emit('leaveRoom', listRooms)
    }

    const onRoomDelete = e => {
      e.preventDefault()


    }

    // if le user à créé la liste
    const renderListCreate = () => {
        return listRooms.map((room, index) => (
          <div className="room_button" key={index}>
            <h4 className="room">
              {room}
            </h4>
            <button className="buttons_join" type="submit" onSubmit={onRoomJoin}>join</button>
            <button className="buttons_leave" type="submit" onSubmit={onRoomLeave}>leave</button>
            <button className="buttons_delete" type="submit" onSubmit={onRoomDelete}>X</button>
          </div>
        ))
    }

    return(
        <div className="list_rooms">
            {renderListCreate()}
        </div>
    )
}

export default ListRooms
