import React, { useState, useEffect } from 'react'
import "bootstrap/dist/css/bootstrap.min.css"
import { Form } from 'react-bootstrap'
import '../css/home.css'
import ListRooms from './listRooms.js'
import socket from './connect.js'


function Room(){

    const [room, setRoom] = useState({room:''})

    const onCreateSubmit = e => {
        e.preventDefault()
        console.log(room)

        socket.emit('createRoom', {newRoom})
        setRoom({ newRoom})
    }

    const onTextChange = e => {
        setRoom({ [e.target.name]: e.target.value })
    }

    return(
        <div>
            <h3 className="titleChat">Channel</h3>
            <ListRooms/>
            <Form className="form_channel" onSubmit={onCreateSubmit}>
                <label>Create new room :</label>
                <input className="inputs_channel" name='newRoom' type='text'onChange={e => onTextChange(e)}></input>
                <button className="buttons_channel" type="submit">create</button>
            </Form>
        </div>
    )
}

export default Room
