import React, { useState } from 'react'
import "bootstrap/dist/css/bootstrap.min.css"
import { Form } from 'react-bootstrap'
import '../css/home.css'
import socket from './connect.js'

function ListRooms(){

    // Affiche List Room
    const [listRooms, setListRooms] = useState([])
    socket.on('listRooms', listRooms => {
      //console.log(listRooms)
      setListRooms(listRooms, listRooms)
    })

    // Room join
    const [listRoomsUser, setListRoomsUser] = useState([])
    socket.on('listRoomsUser', listRoomsUser => {
      setListRoomsUser(listRoomsUser, listRoomsUser)
    })
    const onRoomJoin = room => {
      console.log(room + " : join")
      socket.emit('joinRoom', room)
    }

    // Room Leave
    const onRoomLeave = room => {
      console.log(room + " : leave")
      socket.emit('leaveRoom', room)
    }

    // Delete Room if usercreate
    const onRoomDelete = room => {
      console.log(room + " : delete")
      socket.emit('deleteRoom', room)
    }

    // if le user à créé la liste
    const renderListCreate = () => {
        return listRooms.map((room, index) => (
          <div className="room_button" key={index}>
            <h4 className="room">
              {room}
            </h4>
            <button className="buttons_join" type="submit" onClick={()=>onRoomJoin(room)}>join</button>
            <button className="buttons_join" type="submit" onClick={()=>onRoomEdit(room)}>edit</button>
            <button className="buttons_leave" type="submit" onClick={()=>onRoomLeave(room)}>leave</button>
            <button className="buttons_delete" type="submit" onClick={()=>onRoomDelete(room)}>X</button>
          </div>
        ))
    }

    return(
        <div className="list_rooms">
            {renderListCreate()}
        </div>
    )
}

export default ListRooms
