import React, { useState, useEffect } from 'react'
import TextField from '@material-ui/core/TextField'
import Container from '@material-ui/core/Container'
import Navbar from '../components/navbar.js'
import '../css/home.css'

import socket from './connect.js'

//socket.emit('newUser')

function Home() {

  //déclaration des states 
  const [mess, setMess] = useState({ message: '', name: '' })
  const [chat, setChat] = useState([])


  //Login
  const [namelog, setName] = useState()
  useEffect(() => {

  })


  //déclenché en écrivant, met à jour les valeurs messages
  const onTextChange = e => {
    setMess({ ...mess, [e.target.name]: e.target.value })
  }

  //déclenché par le form, envoi les infos messages au serveur
  const onMessageSubmit = e => {
    e.preventDefault()
    const { name, message } = mess
    socket.emit('message', { name, message })
    setMess({ message: '', name })
  }

  //déclenchement auto, récup évènement messages
  useEffect(() => {
    socket.on('message', ({ name, message }) => {
      setChat([...chat, { name, message }])
    })
  })

  //fin du render chat
  const renderChat = () => {
    return chat.map(({ name, message }, index) => (
      <div key={index}>
        <h3>
          {name} : <span>{message}</span>
        </h3>
      </div>
    ))
  }

  //fin du render All

  const [recupAll, setAll] = useState([])

  socket.on('all', clients => {
    setAll(clients)
  })

  const renderAll = () => {
    console.log(recupAll)
    return recupAll.map((id, index) => (
      <div key={index}>
        <h3>
          {id}
        </h3>
      </div>
    ))
  }



  // room
  socket.emit('create', 'room1');

  //render
  return (
    <div>
    <Navbar/>
    <Container>
    <div className="card">
      <form onSubmit={onMessageSubmit}>
        <h1>Messenger</h1>
        <div className="name-field">
          <TextField
            name="name"
            onChange={e => onTextChange(e)}
            value={mess.name}
            label="Name"
          />
        </div>

        <div>
          <TextField
            name="message"
            onChange={e => onTextChange(e)}
            value={mess.message}
            id="outlined-multiline-static"
            variant="outlined"
            label="Message"
          />
        </div>
        <button>Send Message</button>
      </form>
      <div className="render-chat">
        <h1>Chat Log</h1>
        {renderChat()}
      </div>
      <div>
        <h1>List connected</h1>
        {renderAll()}
      </div>
    </div>
    </Container>
    </div>
  )
}

export default Home
