import React, { useState } from 'react'
import io from 'socket.io-client'
import Container from '@material-ui/core/Container'
import "bootstrap/dist/css/bootstrap.min.css"
import { Row, Form, Button } from 'react-bootstrap';
import '../css/login.css'
import logo from '../assets/logo.jpg';
import {Redirect} from 'react-router-dom'
import { withRouter } from 'react-router';

const socket = io.connect('http://localhost:4000')

class Login extends React.Component{
  constructor() {
    super()
    this.state = {login: ''};
    this.handleChange = this.handleChange.bind(this);
    this.onLoginSubmit = this.onLoginSubmit.bind(this);

  }

  handleChange(event){
    this.setState({login: event.target.login})
    console.log()
    console.log(this.state.login)
  }

  onLoginSubmit(event){
    event.preventDefault()
    console.log(this.state.login)
    socket.emit('loginEnter', this.state.login)
    //this.props.history.push('/home')
  }

  render(){
    return (
      <Container>
        <Row className="justify-content-md-center">
          <h1>Start a new chat on My_Dispute</h1>
        </Row>
        <Row className="justify-content-md-center">
          <Form id="color" onSubmit={this.onLoginSubmit}>
            <Form.Group controlId="Login">
              <Form.Label>Login</Form.Label>
              <Form.Control name ="login" value={this.state.login} onChange={this.handleChange} type="text" required={true}/>
            </Form.Group>

            <div className="buttonLog">
            <Button variant="dark" type="submit">Login</Button>
            </div>
          </Form>
        </Row>
        <div className="welcome_logo">
            <img className="images" src={logo} alt="logo"/>
        </div>
      </Container>
    )
  }

}

export default Login
