import React from 'react'
import '../src/welcome.css'

const socket = io.connect('http://localhost:4000')

function Welcom() {
  const [mess, setMess] = useState({ message: '', name: '' })
  const [chat, setChat] = useState([])


  socket.on('newUser', function(){
    alert('Nouvel utilisateur ' + mess.name)
  })


  useEffect(() => {
    socket.on('message', ({ name, message }) => {
      setChat([...chat, { name, message }])
    })
  })

  const onTextChange = e => {
    setMess({ ...mess, [e.target.name]: e.target.value })
  }

  const onMessageSubmit = e => {
    e.preventDefault()
    const { name, message } = mess
    //console.log(mess.name)
    socket.emit('message', { name, message })
    setMess({ message: '', name })
  }

  const renderChat = () => {
    return chat.map(({ name, message }, index) => (
      <div key={index}>
        <h3>
          {name} : <span>{message}</span>
        </h3>
      </div>
    ))
  }

  return (
    <Container fixed>
    <div className="card">
      <h1>Welcom on chat-chat !</h1>
      <form onSubmit={onMessageSubmit}>
        <h1>Messanger</h1>
        <div className="name-field">
          <TextField
            name="name"
            onChange={e => onTextChange(e)}
            value={mess.name}
            label="Name"
          />
        </div>
        <div>
          <TextField
            name="message"
            onChange={e => onTextChange(e)}
            value={mess.message}
            id="outlined-multiline-static"
            variant="outlined"
            label="Message"
          />
        </div>
        <button>Send Message</button>
      </form>
      <div className="render-chat">
        <h1>Chat Log</h1>
        {renderChat()}
      </div>
    </div>
    </Container>
  )
}

export default Home
